commits.content:
.cart-container {
    background: none;
    padding: 20px;
    max-width: 1200px;
    margin: 0 auto;
  }

  .cart-empty-message {
    text-align: center;
    font-size: 24px;
    color: #555;
    margin-top: 50px;
  }

  .cart-items-container {
    width: 100%;
  }

  .cart-item {
    width: 90%;
    margin: 20px auto;
    padding: 20px;
    background-color: #ffffff;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  }

  .cart-item-content {
    display: flex;
    gap: 20px;
  }

  .cart-item-image {
    width: 300px;
    height: 300px;
    object-fit: cover;
    border-radius: 8px;
  }

  .cart-item-details {
    display: flex;
    flex-direction: column;
    width: 100%;
  }

  .cart-item-description {
    background-color: #f8f9fa;
    padding: 10px;
    border-radius: 4px;
    margin-bottom: 10px;
    height: 100px;
    overflow: auto;
  }

  .cart-item-name {
    font-size: 18px;
    font-weight: bold;
    color: #333;
    margin-bottom: 10px;
  }

  .cart-item-price {
    font-size: 16px;
    color: #555;
    margin-bottom: 10px;
  }

  .cart-item-quantity-controls {
    display: flex;
    gap: 10px;
    align-items: center;
  }

  .cart-item-quantity {
    border: 1px solid #007bff;
    border-radius: 8px;
    color: #007bff;
    font-weight: bold;
    text-align: center;
    padding: 5px 10px;
    background-color: #e9f5ff;
  }

  .cart-item-button {
    border: 1px solid #ccc;
    border-radius: 8px;
    color: #333;
    font-weight: bold;
    background-color: #f8f9fa;
    padding: 5px 10px;
    cursor: pointer;
    transition: background-color 0.3s ease;
  }

  .cart-item-button:hover {
    background-color: #e9ecef;
  }

  .cart-item-delete-button {
    border: 1px solid #dc3545;
    border-radius: 8px;
    color: #dc3545;
    font-weight: bold;
    background-color: transparent;
    padding: 5px 10px;
    cursor: pointer;
    transition: background-color 0.3s ease;
  }

  .cart-item-delete-button:hover {
    background-color: #f8d7da;
  }

  .cart-checkout-button {
    display: block;
    width: 200px;
    margin: 20px auto;
    padding: 10px;
    background-color: #ffc107;
    color: #333;
    border: none;
    border-radius: 8px;
    font-size: 16px;
    font-weight: bold;
    text-align: center;
    cursor: pointer;
    transition: background-color 0.3s ease;
  }

  .cart-checkout-button:hover {
    background-color: #e0a800;
  }
