commits.content:
/* General Styles */
.landing-page {
    font-family: Arial, sans-serif;
    color: #333;
  }

  /* Hero Section */
  .hero-section {
    background-image: url("https://via.placeholder.com/1200x400"); /* Add your hero image */
    background-size: cover;
    background-position: center;
    height: 400px;
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    color: #fff;
  }

  .hero-content h1 {
    font-size: 48px;
    margin-bottom: 20px;
  }

  .hero-content p {
    font-size: 18px;
    margin-bottom: 30px;
  }

  .cta-button {
    background-color: #ff5722;
    color: #fff;
    border: none;
    padding: 10px 20px;
    font-size: 16px;
    cursor: pointer;
    transition: background-color 0.3s;
  }

  .cta-button:hover {
    background-color: #e64a19;
  }

  /* Featured Products Section */
  .featured-products {
    padding: 40px 20px;
    text-align: center;
  }

  .featured-products h2 {
    font-size: 36px;
    margin-bottom: 30px;
  }

  .product-grid {
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
    gap: 20px;
  }

  .product-card {
    background-color: #f9f9f9;
    padding: 20px;
    border-radius: 8px;
    width: 200px;
    text-align: center;
  }

  .product-card img {
    width: 100%;
    height: auto;
    border-radius: 8px;
  }

  .product-card h3 {
    font-size: 18px;
    margin: 10px 0;
  }

  .product-card p {
    font-size: 16px;
    color: #ff5722;
    margin: 10px 0;
  }

  .product-card button {
    background-color: #333;
    color: #fff;
    border: none;
    padding: 10px 20px;
    cursor: pointer;
    transition: background-color 0.3s;
  }

  .product-card button:hover {
    background-color: #555;
  }

  /* Promotional Banner */
  .promo-banner {
    background-color: #ff5722;
    color: #fff;
    padding: 40px 20px;
    text-align: center;
  }

  .promo-banner h2 {
    font-size: 36px;
    margin-bottom: 20px;
  }

  .promo-banner p {
    font-size: 18px;
    margin-bottom: 30px;
  }

  /* Testimonials Section */
  .testimonials {
    padding: 40px 20px;
    text-align: center;
  }

  .testimonials h2 {
    font-size: 36px;
    margin-bottom: 30px;
  }

  .testimonial-grid {
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;
    gap: 20px;
  }

  .testimonial-card {
    background-color: #f9f9f9;
    padding: 20px;
    border-radius: 8px;
    width: 300px;
    text-align: center;
  }

  .testimonial-card p {
    font-size: 16px;
    margin-bottom: 10px;
  }

  .testimonial-card h4 {
    font-size: 14px;
    color: #777;
  }
