commits.content:
.form-container {
    max-width: 500px;
    margin: auto;
    padding: 20px;
    border: 1px solid #ddd;
    border-radius: 5px;
    background-color: #f9f9f9;
  }

  input, select {
    width: 100%;
    padding: 8px;
    margin-bottom: 10px;
  }

  .error {
    color: red;
    font-size: 12px;
  }

  .preview-image {
    width: 100px;
    height: 100px;
    object-fit: cover;
    margin-right: 5px;
  }
