+import "./products.css";
+import { useNavigate } from "react-router-dom";
+import { AuthContext } from "../../AuthContext";
+import { useContext } from "react";
+import { useSelector, useDispatch } from "react-redux";
+import { addToCart, removeFromCart } from "../../cartSlice";
+
+const Products = ({
+  api,
+  loading,
+  images,
+  filteredProducts,
+  setSelectedProduct,
+  addToCart,
